sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if(isLoading || newValue == '')
		{
			return;
		}

	function processResp (resp) {	
		console.log(resp.email+'');
	g_form.setValue('description', resp.email+'');
	}

	var email = g_form.getReference("caller_id");
	if(email) {
	g_form.setValue('description', email.email+'');	
	} else{
		console.log(email);
	}


	jslog("this is debug message");




	g_form.addDecoration('category', 'icon-mail', 'Mail', '#00FF00');
	g_form.addErrorMessage("This is an error");
	g_form.addInfoMessage("This is an Info");
	g_form.addOption('impact', 4, '4- very Low');
	g_form.flash('incident.category','#FFFACD', 2);
	g_form.getControl('number');

}
